import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { PostService } from 'src/app/post.service';
import { Post } from 'src/app/types/post';
import { LoginAuthUser } from 'src/app/types/user';
import { UserService } from 'src/app/user/user.service';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent implements OnInit{

  currentPost: Post = {} as Post;
  user: LoginAuthUser = {} as LoginAuthUser;
  showEditButtons: boolean = false;
  ownerId: string = '';
  loggedUserId: string = '';

  get loggedUser(): LoginAuthUser | undefined {
    return this.userService.user;
  }

  constructor(
    private route: ActivatedRoute,
    private userService: UserService,
    private postService: PostService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((data) => {
      const postId = data['postId'];

      this.postService.getPost(postId).subscribe((post) => {
        this.currentPost = post;
        this.ownerId = post._ownerId;
      });
    });

    if (this.loggedUser) {
      this.user = this.loggedUser;
      this.loggedUserId = this.loggedUser._id;
    }
  }

  deleteHandler(): void {
    this.postService.deletePost(this.currentPost._id).subscribe(() => { 
      this.router.navigate(['/home']);
    });
  }

}



<div class="blog_main">
    <div class="container">
      <div class="row">
        <div class="col-md-12">
          <div class="titlepage">
            <h2>Post Details</h2>
          </div>
        </div>
      </div>
    <app-post-details [currentPost]="currentPost"></app-post-details>
    <div *ngIf="ownerId === loggedUserId">
        <ul class="like">
            <li><a [routerLink]="'/' + 'my-posts/' + currentPost._id + '/edit'"><img src="images/like.png" alt="#"/>Edit</a></li>
             <li><a (click)="deleteHandler()"><img src="images/comm.png" alt="#"/>Delete</a></li>

