import { Location } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { PostService } from 'src/app/services/post.service';
import { Post } from 'src/app/types/post';

@Component({
  selector: 'app-edit-post',
  templateUrl: './edit-post.component.html',
  styleUrls: ['./edit-post.component.css']
})
export class EditPostComponent implements OnInit {
  currentPost: Post = {} as Post;

  form!: FormGroup;

  constructor(private formBuilder: FormBuilder, private postService: PostService, private route: ActivatedRoute, private location: Location) {
    this.createForm({ ...this.currentPost });
  }

  createForm(formValue: Post) {
    this.form = this.formBuilder.group({
      title: [formValue.title],
      content: [formValue.content],
      img: [formValue.img],
      post: [formValue.post]

    })
  }

  ngOnInit(): void {
    this.route.params.subscribe((data) => {
      const postId = data['postId'];
      this.postService.getPost(postId).subscribe((res) => {
        this.createForm({ ...res })
        this.currentPost = res;
      })
    })
  }

  backHandler(e: Event): void {
    e.preventDefault();
    this.location.back()
  }

  editPostHandler(): void {
    if (this.form.invalid) {
      return;
    }

    this.postService.editPost(this.currentPost._id, this.form.value).subscribe()

    this.location.back();
  }

}
<div class="contact">
    <div class="container">
        <div class="container">
          <div class="row">
            <div class="col-md-12">
              <div class="titlepage">
                <h2>Edit Post</h2>
              </div>
            </div>
          </div>
        </div>
        <form class="main_form" [formGroup]="form" (ngSubmit)="editPostHandler()">
        <div class="row justify-content-center">
          <div class="col-md-8">
            <ng-container *ngIf="form.get('title')?.touched">
              <p class="text-red-500" *ngIf="form.get('title')?.errors?.['required']">
                Title is required
              </p>
              <p class="text-red-500" *ngIf="form.get('title')?.errors?.['minlength']">
                Title should be at least three characters long
              </p>
            </ng-container>
            <input
              class="form_contril"
              placeholder="Title"
              type="text"
              name="title"
              id="title"
              #inputTitle="ngModel"
              ngModel
            />
          </div>
          <div class="col-md-8">
            <ng-container *ngIf="form.get('journalEntry')?.touched">
              <p
                class="text-red-500"
                *ngIf="form.get('journalEntry')?.errors?.['required']"
              >
                Text is required
              </p>
              <p
                class="text-red-500"
                *ngIf="form.get('journalEntry')?.errors?.['minlength']"
              >
                Your journal entry should be at least
                {{form.get('journalEntry')?.errors?.['minlength']?.['requiredLength']}}
                characters long, but there are only
                {{form.get('journalEntry')?.errors?.['minlength']?.['actualLength']}}
                characters in the field.
              </p>
            </ng-container>
            <input
              class="form_contril"
              placeholder="Content"
              type="text"
              name="content"
              id="content"
              #inputContent="ngModel"
              ngModel
            />
          </div>
          <div class="col-md-8">
            <input
              class="form_contril"
              placeholder="URL of image"
              type="text"
              name="img"
              id="img"
              #inputImg="ngModel"
              ngModel
            />
          </div>
          <div class="col-md-8">
            <button class="send_btn">Edit</button>
          </div>
        </div>
      </form>
    </div>
  </div>
