import { Component, ContentChild, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { PostService } from 'src/app/post.service';
import { Post } from 'src/app/types/post';

@Component({
  selector: 'app-edit-post',
  templateUrl: './edit-post.component.html',
  styleUrls: ['./edit-post.component.css']
})
export class EditPostComponent implements OnInit{
  currentPost: Post = {} as Post;

  form!: FormGroup;

  constructor(private formBuilder: FormBuilder, private postService: PostService, private route: ActivatedRoute) {
    this.createForm({...this.currentPost});
  }

  createForm(formValue: Post) {
    this.form = this.formBuilder.group({
      title: [formValue.title, [Validators.required]],
      content: [formValue.content, [Validators.required]],
      img: [formValue.img]

    })
  }

  ngOnInit(): void {
    this.route.params.subscribe((data) => {
      const postId = data['postId'];
      this.postService.getPost(postId).subscribe((res) => {
        this.createForm({...res})
        this.currentPost = res;
      })
    })
  }

  editPostHandler(): void {
    if (this.form.invalid) {
      return;
    }

    this.postService.editPost(this.currentPost._id, this.form.value)
  }

}
<div class="contact">
    <div class="container">
        <div class="container">
          <div class="row">
            <div class="col-md-12">
              <div class="titlepage">
                <h2>Edit Post</h2>
              </div>
            </div>
          </div>
        </div>
        <form #form="ngForm" class="main_form" (ngSubmit)="editPostHandler()">
        <div class="row justify-content-center">
          <div class="col-md-8">
            <input
              class="form_contril"
              placeholder="Title"
              type="text"
              name="title"
              id="title"
              #inputTitle="ngModel"
              ngModel
            />
          </div>
          <div class="col-md-8">
            <input
              class="form_contril"
              placeholder="Content"
              type="text"
              name="content"
              id="content"
              #inputContent="ngModel"
              ngModel
            />
          </div>
          <div class="col-md-8">
            <input
              class="form_contril"
              placeholder="URL of image"
              type="text"
              name="img"
              id="img"
              #inputImg="ngModel"
              ngModel
            />
          </div>
          <div class="col-md-8">
            <button class="send_btn">Edit</button>
          </div>
        </div>
      </form>
    </div>
  </div>
